  width: 100%;
  height: 200px;
  border: 1px black solid;
`;

const Logo = styled.h1`
  font-size: 68px;
  line-height: 200px;
`;

AppLayout.propTypes = {
  children: Proptypes.node.isRequired,
};

export default AppLayout;
